INSERT INTO collection (collection_id, author_id, collection_name, description, creation_time, picture_url)
VALUES (1, 1, 'Для ценителей Хогвартса',
        'Мы собрали несколько фильмов для тех, кто мечтает вернуться в мир, где волшебство — нормальное явление, а с героями происходят удивительные, порой страшные, а порой смешные события',
        current_timestamp, '/static/media/img/collections/1.webp'),
       (2, 1, 'Для подростков',
        'Лучшие фильмы мирового кинематографа, главными действующими лицами которых являются подростки',
        current_timestamp, '/static/media/img/collections/2.webp'),
       (3, 1, 'Приключения',
        'Приключения – что может быть еще интересней и захватывающей? Приключенческий жанр фильмов очень познавателен и востребован, именно он позволяет отвлечься от повседневных забот и окунуться в мир, полный благородных и мужественных персонажей. Смотреть лучшие приключения можно всей семьей, что увеличивает их популярность.',
        current_timestamp, '/static/media/img/collections/3.webp'),
       (4, 2, 'Романтические фильмы',
        'Романтические фильмы пользовались и будут пользоваться популярностью во все времена. Каждая девушка любит смотреть онлайн красивую и страстную историю любви, и нет ничего лучше, чем разделить это удовольствие со своей второй половинкой.',
        current_timestamp, '/static/media/img/collections/4.webp'),
       (5, 2, 'Летнее настроение',
        'Вас ждут солнечные комедии, мелодрамы и даже криминальные фильмы, пропитанные летним настроением. Посмотрите их все и выберите любимый.',
        current_timestamp, '/static/media/img/collections/5.webp'),
       (6, 2, 'Аферы века',
        'Фильмы про аферы всегда интересны и захватывающи, ведь их главными героями становятся неординарные личности с острым умом и потрясающей хитростью, поражающие зрителей своей изобретательностью и разнообразием методов и приемов. Да, это всегда гениальные мошенники, зачастую преследующие цель в виде крупной наживы. Но тем интереснее смотреть за развитием сюжета, ведь противостоят им, как правило, не менее толковые полицейские и сыщики. Фильмы про аферы никогда не оставляют зрителей равнодушными.',
        current_timestamp, '/static/media/img/collections/6.webp'),
       (7, 6, 'Фильмы на вечер',
        'Если на сегодняшний вечер у вас нет особых планов, а провести его хочется с приятностью, мы подобрали несколько интересных фильмов на самые разные вкусы.',
        current_timestamp, '/static/media/img/collections/7.webp'),
       (8, 6, 'Про Вторую мировую',
        'Фильмы о беспощадной и страшной Второй мировой войне играют большую роль в кинематографе, как отечественном, так и зарубежном. На сегодняшний день это самый крупный вооруженный конфликт, втянувший в себя десятки стран и миллионы человеческих судеб. В основе многих фильмов лежат реальные, имевшие место в прошлом события.',
        current_timestamp, '/static/media/img/collections/8.webp'),
       (9, 6, 'Время героев',
        'Основные персонажи фильмов этой категории не по наслышке знают, что такое быть героем, ведь на их плечи легло испытание войной. Брошенные в жестокий мир взаимного уничтожения, где сама человечность размыливается на фоне хаоса и постоянной угрозе гибели, эти герои не потеряли веру в человечество, и готовы идти на самопожертвование ради достижения задачи или спасения своих боевых товарищей.',
        current_timestamp, '/static/media/img/collections/9.webp'),
       (10, 6, 'Лучшие боевики',
        'Лучшие боевики - это фильмы, где снимаются только самые популярные актеры, способные или сами или с помощью дублеров показать на экранах максимум зрелищности в драках, погонях и перестрелках. Ведь этим и отличается данный жанр кинематографа.',
        current_timestamp, '/static/media/img/collections/10.webp'),
       (11, 3, '20 век.История',
        'История ХХ века в кино – уникальная возможность стать свидетелем величайших открытий, самых известных сражений и проследить за судьбой именитых деятелей.',
        current_timestamp, '/static/media/img/collections/11.webp'),
       (12, 1, 'Лучшие экранизации',
        'Нет времени почитать бестселлер, который вам посоветовали все друзья? Не беда! Мы составил список лучших экранизаций бестселлеров последних лет. Смотрите с удовольствием!',
        current_timestamp, '/static/media/img/collections/12.webp'),
       (13, 1, 'Лучшее в 2021 году',
        'Самые топовые фильмы, сериалы и мультфильмы, сделавшие 2021-ый киногод особенным и незабываемым',
        current_timestamp, '/static/media/img/banners/2021.webp'),
       (14, 1, 'Мотивация учиться',
        'Подборка фильмов, после просмотра которых действительно хочется крепко взяться за учебу',
        current_timestamp, '/static/media/img/banners/studyHard.webp');


